"""
Module to store the Reborn logo as base64 for use in the Streamlit app.
This PNG image has been properly encoded with transparency support.
"""

def get_reborn_logo_base64():
    """
    Return the Reborn logo encoded in base64.
    1. Prefer an on-disk image (PNG preferred, JPEG fallback) so that the logo can be updated without touching code.
    2. If the file is not found or cannot be read, fall back to the bundled placeholder string so the app still works.
    """
    import base64, os, logging

    logger = logging.getLogger("reborn_logo")

    possible_paths = [
        os.path.join(os.path.dirname(__file__), "static", "reborn_logo.png"),  # new preferred path
        os.path.join(os.path.dirname(__file__), "Reborn Logo.jpeg"),            # legacy root path
        os.path.join(os.path.dirname(__file__), "Reborn_Logo.png"),            # alternate naming
    ]

    for path in possible_paths:
        if os.path.isfile(path):
            try:
                with open(path, "rb") as img_file:
                    b64_data = base64.b64encode(img_file.read()).decode("utf-8")
                    # Cache the encoded string in a module attribute so we don't re-read disk each call
                    globals()["_REBORN_LOGO_B64"] = b64_data
                    return b64_data
            except Exception as e:
                logger.warning(f"Could not read logo file '{path}': {e}")

    # Fallback to the hard-coded placeholder if no file found / readable
    logger.warning("Using bundled placeholder logo – consider adding 'static/reborn_logo.png' for best quality.")
    return """"""
